import React from 'react';
import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
import { CreateTodoButton } from './CreateTodoButton';
import './App.css';

const todos = [
  { text: 'Cortar cebolla', value: '100', completed: false},
  { text: 'Tomar el curso de platzi', value: '80', completed: false},
  { text: 'llorar con la llorona', value: '20', completed: false},
];

function TodoPop(){
	return(
		<React.Fragment>
        <TodoCounter/>
        <TodoSearch/>
        <CreateTodoButton/>
        <TodoList>
          {todos.map(todo => (
            <TodoItem key={todo.text} text={todo.text} value={todo.value} />
            ))}
        </TodoList>
    	</React.Fragment>
	);
}

export { TodoPop };
