import React from 'react';
import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
import { CreateTodoButton } from './CreateTodoButton';
import './App.css';

const todos = [
  { id: 1,text: 'Cortar cebolla', value: '100', completed: false},
  { id: 1,text: 'Tomar el curso de platzi', value: '80', completed: false},
  { text: 'llorar con la llorona', value: '20', completed: false},
]

function App() {
  return(
    <React.Fragment>
        <TodoCounter/>
        <TodoSearch/>
        <CreateTodoButton/>
        <TodoList>
          {todos.map(todo => (
            <TodoItem key={todo.id} text={todo.text} value={todo.value} />
            ))}
        </TodoList>
    </React.Fragment>
  );
}

export default App;
