import React from 'react';
import './TodoItem.css';


function TodoItem(props){
	return(
		<table className="TodoItem">
		<thead>
			<tr>
				<th><span className={`Icon Icon-check ${props.completed&&'Icon-check--active'}`}>âˆš</span></th>
				<th><p className={`TodoItem-p ${props.completed&&'TodoItem-p--complete'}`}>{props.text}</p></th>
				<th><p className="TodoItem-value">{props.value}%</p></th>
				<th>
					<div className="loadProgres_todo">
					<div className="loadProgres" value={props.value} max="100"></div>
					<div className="loadProgres loadProgres--marc" style={{width:`${props.value}%`}}></div>
					</div>
				</th>
				<th><span className="Icon Icon-delete">X</span></th>
			</tr>
		</thead>
		</table>

	);
}

export { TodoItem };
