import React from 'react';
import './TodoItem.css';
import { faXmark,faCheck } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function TodoItem(props){
	return(
		<section className="TodoItem">
			<span className={`Icon Icon-check ${props.completed&&'Icon-check--active'}`}><FontAwesomeIcon icon={faCheck}/></span>
			<p className={`TodoItem-p ${props.completed&&'TodoItem-p--complete'}`}>{props.text}</p>
			<span className="Icon Icon-delete Icon-disable"><FontAwesomeIcon icon={faXmark}/></span>
			<p className="TodoItem-value">{props.value}%</p>
			<div className="loadProgres_todo">
			<div className="loadProgres" value={props.value} max="100"></div>
			<div className="loadProgres loadProgres--marc" style={{width:`${props.value}%`}}></div>
			</div>
			<span className="Icon Icon-delete"><FontAwesomeIcon icon={faXmark}/></span>
		</section>
	);
}

export { TodoItem };
