import './TodoItem.css';

function TodoItem(props){
	return(
		<li className="TodoItem">
			<span className={`Icon Icon-check ${props.completed&&'Icon-check--active'}`}>âˆš</span>
			<p className={`TodoItem-p ${props.completed&&'TodoItem-p--complete'}`}>{props.text}</p>
			<div className="loadProgres_todo">
			<div className="loadProgres" value={props.value} max="100">${props.value}%</div>
			<div className="loadProgres loadProgres--marc"></div>
			</div>
			<span className="Icon Icon-delete">X</span>
		</li>
	);
}

export { TodoItem };
