import React from 'react';
import './TodoItem.css';


function TodoItem(props){
	return(
		<React.Fragment>
		<section className="TodoItem">
			<td><span className={`Icon Icon-check ${props.completed&&'Icon-check--active'}`}>âˆš</span></td>
			<td><p className={`TodoItem-p ${props.completed&&'TodoItem-p--complete'}`}>{props.text}</p></td>
			<td><p className="TodoItem-value">{props.value}%</p></td>
			<td>
				<div className="loadProgres_todo">
				<div className="loadProgres" value={props.value} max="100"></div>
				<div className="loadProgres loadProgres--marc" style={{width:`${props.value}%`}}></div>
				</div>
			</td>
			<td><span className="Icon Icon-delete">X</span></td>
		</section>
		</React.Fragment>
	);
}

export { TodoItem };
