import React from 'react';
import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';
import { TodoList } from './TodoList';
import { TodoItem } from './TodoItem';
import { CreateTodoButton } from './CreateTodoButton';
import './TodoPop.css';

const todos = [
  { text: 'Cortar cebolla', value: '100', completed: true},
  { text: 'Tomar el curso de platzi', value: '80', completed: false},
  { text: 'llorar con la llorona', value: '69', completed: false}
];

function TodoPop(){
	return(
		<React.Fragment>
		<section className="TodoBox">
    <section className="TodoBox--marc"></section>
        <TodoCounter/>
        <TodoSearch/>
        <CreateTodoButton/>
        <TodoList>
          {todos.map(todo => (
            <TodoItem key={todo.text} text={todo.text} value={todo.value} />
            ))}
        </TodoList>
    	</section>
    	</React.Fragment>
	);
}

export { TodoPop };
