import React from 'react';
import { TodoPopUi } from './TodoPopUi';
import './TodoPop.css';

const defalultTodos = [
  { text: 'Cortar cebolla', value: '100', completed: true, progress: '01:00'},
  { text: 'Tomar el curso de platzi', value: '80', completed: false, progress: '00:00'},
  { text: 'llorar con la llorona', value: '69', completed: true, progress: '00:00'}
];

function TodoPop(){
  const [todos, setTodos] = React.useState(defalultTodos);
  const [searchValue, setSearchValue] = React.useState('');
  const completedTodos = todos.filter(todo => todo.completed).length;
  const totalTodos = todos.length;
  const createButtton = document.getElementById("createButton");

  let searchedTodos = [];

  if (!searchValue.length >= 1){
    searchedTodos = todos;
  }else{
    searchedTodos = todos.filter(todo => {
      const todoText = todo.text.toLowerCase();
      const searchText = searchValue.toLowerCase();
      return todoText.includes(searchText);
    });
    TodoCreate();
  }

  const CompleteTodos = (text) =>{
    const todoIndex = todos.findIndex(todo => todo.text === text)
    const newTodos = [...todos];
    newTodos[todoIndex].completed = true;
    setTodos(newTodos);
  };


function TodoCreate(){
  if (searchedTodos.length <= 0){
    createButtton.classList.remove("Disable-button");
  }else{
    createButtton.classList.add("Disable-button");
  };
}
	return(
    <TodoPopUi
      totalTodos={totalTodos}
      completedTodos={completedTodos}
      searchValue={searchValue}
      setSearchValue={setSearchValue}
      searchedTodos={searchedTodos}
      onComplete={()=>completedTodos(todo.text)}
    />
  );
}

export { TodoPop };
