import React from 'react';
import { TodoPopUi } from './TodoPopUi';
import './TodoPop.css';

// const defaultTodos = [
//   { text: 'Cortar cebolla', value: '100', completed: true, progress: '01:00'},
//   { text: 'Tomar el curso de platzi', value: '80', completed: false, progress: '00:00'},
//   { text: 'llorar con la llorona', value: '69', completed: true, progress: '00:00'}
// ];
function useLocalStorage(itemName, initialValue){
  const [err, setErr] = React.useState(false);
  const [loading, setLoading] = React.useState(true);
  const [item, setItem] = React.useState(initialValue);
  React.useEffect(() => {
    setTimeout(()=>{
      try{
        const localStorageItem = localStorage.getItem(itemName);
        let parsedItem;
        if(!localStorageItem){
          localStorage.setItem(itemName, JSON.stringify(initialValue));
          parsedItem = initialValue;
        }else{
          parsedItem = JSON.parse(localStorageItem);
      }
      setItem(parsedItem);
      setLoading(false);
      }catch(err){
        setErr(err);
      }
    },3000);
  });
  const saveItem = (newItem)=>{
    try{
      const stringifiedItem  = JSON.stringify(newItem);
      localStorage.setItem(itemName, stringifiedItem)
      setItem(newItem);
    }catch(err){
      setErr(err);
    }
  };
  return {
    item,
    saveItem,
    loading,
    err,
  };

}


function TodoPop(){
  const {
    item: todos,
    setItem: saveTodos,
    loading,
    err,
  } = useLocalStorage("TODOS_V1", []);
  const [searchValue, setSearchValue] = React.useState('');
  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;
  let searchedTodos = [];
  if (!searchValue.length >= 1) {
    searchedTodos = todos;
  } else {
    searchedTodos = todos.filter(todo => {
      const todoText = todo.text.toLowerCase();
      const searchText = searchValue.toLowerCase();
      return todoText.includes(searchText);
    });
    const createButtton = document.getElementById("createButton");
      if (searchedTodos.length <= 0){
        createButtton.classList.remove("Disable-button");
      }else{
        createButtton.classList.add("Disable-button");
      };
  }

  const completeTodos = (text) => {
    const todoIndex = todos.findIndex(todo => todo.text === text);
    const newTodos = [...todos];
    newTodos[todoIndex].completed = true;
    saveTodos(newTodos);
  };

  const deleteTodo = (text) => {
    const todoIndex = todos.findIndex(todo => todo.text === text);
    const newTodos = [...todos];
    newTodos.splice(todoIndex, 1);
    saveTodos(newTodos);
  };
	return(
    <TodoPopUi
      loading={loading}
      totalTodos={totalTodos}
      completedTodos={completedTodos}
      searchValue={searchValue}
      setSearchValue={setSearchValue}
      searchedTodos={searchedTodos}
      completeTodos={completeTodos}
      deleteTodo={deleteTodo}
    />
  );
}

export { TodoPop }
