import React from 'react';
import './TodoItem.css';
import { faXmark,faCheck } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function TodoItem(props){
	return(
			<thead className="TodoItem">
				<tr>
					<th><span className={`Icon Icon-check ${props.completed&&'Icon-check--active'}`}><FontAwesomeIcon icon={faCheck}/></span></th>
					<th><p className={`TodoItem-p ${props.completed&&'TodoItem-p--complete'}`}>{props.text}</p></th>
					<th><span className="Icon Icon-delete"><FontAwesomeIcon icon={faXmark}/></span></th>
					<th><p className="TodoItem-value">{props.value}%</p></th>
					<th>
						<div className="loadProgres_todo">
						<div className="loadProgres" value={props.value} max="100"></div>
						<div className="loadProgres loadProgres--marc" style={{width:`${props.value}%`}}></div>
						</div>
					</th>
					<th><span className="Icon Icon-delete"><FontAwesomeIcon icon={faXmark}/></span></th>
				</tr>
			</thead>

	);
}

export { TodoItem };
