import React from 'react';
import './TodoItem.css';
import { faXmark,faCheck } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function TodoItem(props){

	const onComplete = () =>{
		alert(' Completaste la tarea '+ props.text);
	};
	const onDelete = () =>{
		alert(' Eliminaste la tarea '+ props.text);
	};
	const onOpen = () =>{
  	document.getElementById("TodoDate").classList.remove('Disable-content');
		alert(' Abriste la Tarea ' + props.text);
	};
	return(
		<React.Fragment>
		<section className="TodoItem" onClick={onOpen}>
			<span className={`Icon Icon-check ${props.completed && 'Icon-check--active'}`}
			onClick={onComplete}
			>
			<FontAwesomeIcon icon={faCheck}/>
			</span>
			<p className={`TodoItem-p ${props.completed&&'TodoItem-p--complete'}`}>{props.text}</p>
			<span id="deleteBtnMob" className="Icon Icon-delete"
			onClick={onDelete}
			>
			<FontAwesomeIcon icon={faXmark}/>
			</span>
			<p className="TodoItem-value">{props.value}%</p>
			<div className="loadProgres_todo">
			<div className="loadProgres" value={props.value} max="100"></div>
			<div className="loadProgres loadProgres--marc" style={{width:`${props.value}%`}}></div>
			</div>
			<span id="deleteBtnDesk" className="Icon Icon-delete Icon-disable"
			onClick={onDelete}
			>
			<FontAwesomeIcon icon={faXmark}/>
			</span>
		</section>
		<section className="TodoDate Disable-content">
			<p>{props.progress}</p>
		</section>
		</React.Fragment>
	);
}

export { TodoItem };
